module.exports = function (target, opts) {
  opts = opts || {};
  var evt = document.createEvent("MouseEvents");
  evt.initMouseEvent(opts.type || 'click',
                     opts.canBubble || true,
                     opts.cancelable || true,
                     opts.view || window,
                     opts.detail || null,
                     opts.screenX || 0,
                     opts.screenY || 0,
                     opts.clientX || 0,
                     opts.clientY || 0,
                     opts.ctrlKey || false,
                     opts.altKey || false,
                     opts.shiftKey || false,
                     opts.metaKey || false,
                     opts.button || 0,
                     opts.relatedTarget || null);
  evt.which = opts.which || 1;
  target.dispatchEvent(evt);
};
